mapper.insert:

insert into ajdzjzwj (ID, AJDZJZMLID, FILENAME, ORGNAME,
  FLAG, OLDSORT, OCRSORT, 
  NEWSORT, UPLOAD_TIME, OCRCONTENT
  )
values (#{id,jdbcType=VARCHAR}, #{ajdzjzmlid,jdbcType=VARCHAR}, #{filename,jdbcType=VARCHAR},#{orgname,jdbcType=VARCHAR},
  #{flag,jdbcType=INTEGER}, #{oldsort,jdbcType=INTEGER}, #{ocrsort,jdbcType=INTEGER}, 
  #{newsort,jdbcType=INTEGER}, #{uploadTime,jdbcType=TIMESTAMP}, #{ocrcontent,jdbcType=LONGVARCHAR}
  )

mapper.select:
select wj.ID, wj.AJDZJZMLID, wj.FILENAME, wj.ORGNAME, wj.OCRCONTENT, wj.FLAG, wj.OLDSORT, wj.OCRSORT, wj.NEWSORT, wj.UPLOAD_TIME from ajdzjzml ml
left join ajdzjzwj wj on wj.AJDZJZMLID = ml.ID
where PARENTID=#{parentid} and ml.AJBS = #{ajbs}

select max(newsort) from ajdzjzwj wj
left join ajdzjzml ml on ml.id = wj.AJDZJZMLID
where ml.TYPENAME = #{typename} and ml.AJBS = #{ajbs}

select wj.ID from ajdzjzwj wj
left join ajdzjzml ml on ml.ID = wj.AJDZJZMLID
where ml.ORDERBY = #{pid} and wj.OLDSORT = #{oldsort} and ml.AJBS = #{ajbs}

select wj.ID, wj.AJDZJZMLID, wj.FILENAME, wj.ORGNAME, wj.FLAG, wj.OLDSORT, wj.OCRSORT, wj.NEWSORT, wj.UPLOAD_TIME from ajdzjzwj wj
left join ajdzjzml ml on ml.id = wj.ajdzjzmlid
where ml.ajbs = #{ajbs}

select max(newsort) from ajdzjzwj wj
left join ajdzjzml ml on ml.id = wj.AJDZJZMLID
where ml.ID = #{mlid} and ml.AJBS = #{ajbs}

select wj.ID, wj.AJDZJZMLID, wj.FILENAME, wj.ORGNAME, wj.FLAG, wj.OLDSORT, wj.OCRSORT, wj.NEWSORT, wj.UPLOAD_TIME  from ajdzjzwj wj
left join ajdzjzml ml on wj.AJDZJZMLID = ml.ID
where ajbs = #{ajbs} and oldsort = #{oldsort}
